    private void jFileChooser1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jFileChooser1ActionPerformed
        if(jFileChooser1.getSelectedFile().getPath().endsWith(".png")||jFileChooser1.getSelectedFile().getPath().endsWith(".jpg")||jFileChooser1.getSelectedFile().getPath().endsWith(".jpeg")){
            vista.imagen.setText(jFileChooser1.getSelectedFile().getPath());            
            this.setVisible(false);
        }else{
            try {
                throw new Exception("formato no permitido(.png , .jpg, .jpeg)");
            } catch (Exception ex) {
                JOptionPane.showMessageDialog(null,ex.getMessage());
            }
        }
        
    }//GEN-LAST:event_jFileChooser1ActionPerformed

    
    

